import React, { useEffect, useState } from 'react';
import './App.css';

import ScrenDisplay from './components/ScreenDisplay'
import Display from './components/Display';

function App() {

	//Dto
	const dto = {
		accId : 0,
		operator : "",
		begin : "",
		end :""
	}

	//useState
	const [extrato, setExtrato] = useState([]);
	const [objExtrato, setObjExtrato] = useState(dto);


	//getFormData
	const getFormData = (e) => {
		setObjExtrato({...objExtrato, [e.target.name]:e.target.value});
	}


	//teste
	const getByOperator = () => {
		fetch("http://localhost:8080/transaction/operator", 
			{

				method: "GET", 
				body: JSON.stringify(objExtrato),
				mode: 'cors',
				headers: {
					'Content-Type': 'application/json',
				}
			}
		).then(response => response.json())
		.them(convertedResponse => setExtrato(convertedResponse))
	}

	/*
	//useEffect
	useEffect(() => {
		fetch("http://localhost:8080/transaction")
		.them(response => response.json())
		.them(convertedResponse => setExtrato(convertedResponse))
	},[]);
*/
	//retorno
  return (
    <div className='App'>
		<ScrenDisplay keyboardEvent={getFormData} onClickEvent/>
		<Display vetor={extrato}/>
    </div>
  );
}
export default App;
