import React, { useEffect, useState } from 'react';
import './App.css';
import axios from "axios";


import ScrenDisplay from './components/ScreenDisplay'
import Display from './components/Display';

function App() {

	//Dto
	const dto = {
		accId : 0,
		operator : "",
		begin : "",
		end :""
	}

	//useState
	const [extrato, setExtrato] = useState([]);
	const [objExtrato, setObjExtrato] = useState(dto);


	//getFormData
	const getFormData = (e) => {	
		setObjExtrato({...objExtrato, [e.target.name]:e.target.value});
	}


	//teste

	function checkTypeRequest() {
		if (!objExtrato.begin && !objExtrato.end && !objExtrato.accId){
			getByOperator();
		} else if (!objExtrato.operator && !objExtrato.accId){
			getByDate();
		} else if (objExtrato.begin && objExtrato.end && objExtrato.operator){
			getByAll();
		}
	}

	const getByOperator = () => {

		const url = ("http://localhost:8080/transaction/operator?param="+[objExtrato.operator]); 
		const header = {
			'Access-Control-Request-Headers': 'Content-Type'
		}
		axios.get(url, { header })
			.then((response) => {

				setExtrato(response.data)
				})
			.catch((error) => {
				console.log(error);
			})
	}

	const getByDate = () => {
		const url = ("http://localhost:8080/transaction/date?param1="+[objExtrato.begin]+"&param2="+[objExtrato.end]); 
		const header = {
			'Access-Control-Request-Headers': 'Content-Type'
		}
		axios.get(url, { header })
			.then((response) => {
				console.log(response.data)
				setExtrato(response.data)
				})
			.catch((error) => {
				console.log(error);
			})
	}

	const getByAll = () => {
		const url = ("http://localhost:8080/transaction/all?param1="+[objExtrato.begin]+"&param2="+[objExtrato.end]+"&param3="+[objExtrato.operator]);
		const header = {
			'Access-Control-Request-Headers': 'Content-Type'
		}
		axios.get(url, { header })
			.then((response) => {
				setExtrato(response.data)
				})
			.catch((error) => {
				console.log(error);
			})
	}

  return (
    <div className='App'>
		<ScrenDisplay keyboardEvent={getFormData} onClickEvent={checkTypeRequest}/>
		<Display vetor={extrato}/>
    </div>
  );
}
export default App;
