import './App.css';

import ScrenDisplay from './components/ScreenDisplay'
import Display from './components/Display';

function App() {

	//Dto
	const dto = {
		accId : 0,
		operator : "",
		begin : "",
		end :""
	}

	//useState
	const [extrato, setExtrato] = useState([]);
	const [objExtrato, setObjExtrato] = useState(dto);


	//getFormData
	const getFormData = (e) => {
		console.log(e.target)
		setObjExtrato
	}

	/*
	//useEffect
	useEffect(() => {
		fetch("http://localhost:8080/transaction")
		.them(response => response.json())
		.them(convertedResponse => setExtrato(convertedResponse))
	},[]);
*/
	//retorno
  return (
    <div className='App'>
		<p>{JSON.stringify(objExtrato)}</p>
		<ScrenDisplay keyboardEvent={getFormData}/>
		<Display vetor={extrato}/>
    </div>
  );
}
export default App;
