import React, { useEffect, useState } from 'react';
import './App.css';
import axios from "axios";


import ScrenDisplay from './components/ScreenDisplay'
import Display from './components/Display';

function App() {

	//Dto
	const dto = {
		accId : 0,
		operator : "",
		begin : "",
		end :""
	}

	//useState
	const [extrato, setExtrato] = useState([]);
	const [objExtrato, setObjExtrato] = useState(dto);


	//getFormData
	const getFormData = (e) => {	
		setObjExtrato({...objExtrato, [e.target.name]:e.target.value});
	}


	//teste

	function checkTypeRequest() {
		if (!objExtrato.begin && objExtrato.end && objExtrato.accId){
			getByOperator;
		}
	}

	const getByOperator = () => {
		const url = ("http://localhost:8080/transaction/operator?param="+[objExtrato.operator]); 
		const header = {
			'Access-Control-Request-Headers': 'Content-Type'
		}
		axios.get(url, { header })
			.then((response) => {
				setExtrato(response.data)
				})
			.catch((error) => {
				console.log(error);
			})
	}

	/*
	account: Object { idAcc: 1, name: "Fulano" }
	​​​
	id: 5
​​​
	operatorName: "Beltrano"
​​​
	transactionType: "TRANSFERENCIA"
​​​
	transferDate: "2020-06-08T07:15:01.000+00:00"
​​​
value: 3241.23
	//useEffect
	useEffect(() => {
		fetch("http://localhost:8080/transaction")
		.them(response => response.json())
		.them(convertedResponse => setExtrato(convertedResponse))
	},[]);
*/
	//retorno
  return (
    <div className='App'>
		<ScrenDisplay keyboardEvent={getFormData} onClickEvent={check}/>
		<Display vetor={extrato}/>
    </div>
  );
}
export default App;
